import React, { useState } from 'react'

const Prices = ({ bpi }) => {
    const [currency, setCurrency] = useState(bpi.USD)

    return (
        <div>
            <ul className="list-group">
                <li className="list-group-item">
                    Bitcoin rate for {} : <span className="badge badge-primary">{bpi.USD}</span> <strong>{bpi.USD}</strong>
                </li>
            </ul>
        </div>
    )
}

export default Prices
