import React, { useState } from 'react'

const Prices = ({ bpi }) => {
    const [currency, setCurrency] = useState('USD')
    const code = bpi.USD.code;
    const rate = bpi.USD.rate;
    const desc = bpi.USD.description
    console.log(bpi);
    
    return (
        <div>
            <ul className="list-group">
                <li className="list-group-item">
                    Bitcoin rate for {desc} : <span className="badge badge-primary">{code}</span> <strong>{rate}</strong>
                </li>
            </ul>
        </div>
    )
}

export default Prices
